        private static void InsertLiveOdds(Session session, LiveOdds r) {

                LiveOddsHistory h = new LiveOddsHistory(r.getApsBookMaker(), r.getApsExchange(), r.getApsBackUrl(), r.getApsExchangeUrl(), r.getApsbackOdds(), r.getApslayOdds(), r.getApsOutcomeName(), r.getApsCondition(), r.getApsEventName(), r.getApsDifferencePercentage(),
                                r.getLastUpdated(), r.getApsMatchTime());
                session.beginTransaction();
                try {
                        session.save(r);
                        session.save(h);
                        session.getTransaction().commit();
                } catch (Exception ex) {
                        session.getTransaction().rollback();
                        ex.printStackTrace();
                }
        }

