        public static void SendMail(String email, String body, String subject) {
                try {

                        Properties props = new Properties();
                        props.put("mail.smtp.host", adminEmails.get(0).getHost().trim());
                        // smtp.gmail.com
                        props.put("mail.smtp.auth", "true");
                        props.put("mail.debug", "true");
                        props.put("mail.smtp.starttls.enable", "true");
                        props.put("mail.smtp.port", adminEmails.get(0).getPort().trim());
                        props.put("mail.smtp.socketFactory.port", adminEmails.get(0).getPort().trim());
                        // props.put("mail.smtp.socketFactory.class",
                        // "javax.net.ssl.SSLSocketFactory");
                        props.put("mail.smtp.socketFactory.fallback", "false");

                        javax.mail.Session mailSession = javax.mail.Session.getInstance(props, new javax.mail.Authenticator() {

                                protected PasswordAuthentication getPasswordAuthentication() {
                                        return new PasswordAuthentication(adminEmails.get(0).getEmail().trim(), adminEmails.get(0).getPass().trim());
                                }
                        });

                        //mailSession.setDebug(true); // Enable the debug mode

                        Message msg = new MimeMessage(mailSession);

                        // --[ Set the FROM, TO, DATE and SUBJECT fields
                        msg.setFrom(new InternetAddress(adminEmails.get(0).getEmail().trim()));
                        msg.setRecipients(Message.RecipientType.TO, InternetAddress.parse(email));
                        // msg.setSentDate(new Date());
                        msg.setSubject(subject);

                        // --[ Create the body of the mail
                        // msg.setText(body);
                        msg.setContent(body, "text/html; charset=utf-8");

                        // --[ Ask the Transport class to send our mail message
                        Transport.send(msg);

                } catch (Exception E) {
                        System.out.println("Oops something has gone pearshaped!");
                        System.out.println(E);
                }
        }

